import pako from "pako";
export function gzip(buffer) { return Promise.resolve(pako.gzip(buffer)); }
export function gzipSync(buffer) { return pako.gzip(buffer); }
export function ungzip(buffer) { return Promise.resolve(pako.ungzip(buffer)); }
export function gunzipSync(buffer) { return pako.ungzip(buffer); }
export function inflate(buffer) { return Promise.resolve(pako.inflate(buffer)); }
export function deflate(buffer) { return Promise.resolve(pako.deflate(buffer)); }
export function inflateSync(buffer) { return pako.inflate(buffer); }
export function deflateSync(buffer) { return pako.deflate(buffer); }
